import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as s,c as i,a as e,b as t,d as o,e as c}from"./app-ae462930.js";const l={},h=e("h1",{id:"the-preservation-and-restoration-of-architectural-mounments",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#the-preservation-and-restoration-of-architectural-mounments","aria-hidden":"true"},"#"),t(" The Preservation and Restoration of Architectural Mounments")],-1),d={href:"https://www.icomos.org/charters/gardens_e.pdf",target:"_blank",rel:"noopener noreferrer"},_=e("br",null,null,-1),p={href:"https://icomosjapan.org/static/homepage/charter/others1904.pdf",target:"_blank",rel:"noopener noreferrer"},u=c('<h2 id="記念建造物の保存と修復" tabindex="-1"><a class="header-anchor" href="#記念建造物の保存と修復" aria-hidden="true">#</a> 記念建造物の保存と修復</h2><p>第６回国際建築家会議、マドリッド（1904 年）</p><ol><li>記念建造物はふたつの種類に分けられる。死んだ記念建造物、それらは過去の文明に属しているか、あるいはもう廃れてしまった目的に仕えているものである。生きた記念建造物、それらは本来意図された目的に仕えつづけているものである。</li><li>死んだ記念建造物は、廃墟と化してしまうのをくいとめるために必要な補強などを施すことによってのみ保存されるべきである。なぜならこのような記念建造物の重要性は、その歴史的・技術的価値にあり、それらの価値は記念建造物自体とともに消滅してしまうものだからである。</li><li>生きた記念建造物は、使われ続けられるように修復されるのが適切である。なぜなら建築にとって、有益性は美しさの根拠のひとつである。</li><li>様式の統一もまた、建築にとって美しさのもう一つの根拠であり、基本的な幾何学形態は完全に再現することが出来るのだから、このような修復は記念建造物がその統一性を保存できるように、本来の様式によってなされるべきである。もし、全体とは異なる様式で作られた部分が本質的な長所を有しており、この記念建造物の審美的調和を崩さないのであれば、この部分は尊重されるべきである。</li><li>記念建造物の保存と修復は、「国家資格を得た」建築家か、あるいは特 に権限を与えられ、国家による美的・考古学的・技術的管理下で働く建築家にのみ、委ねられるべきである。</li><li>歴史的、美的記念建造物を保存するための団体を各国につくるべきである。彼らは、国そして地方の財産の一 目録作成に際して、共 の努力のために一団となり、共同して働くだろう。</li></ol><p>（池亀　訳）</p>',4);function f(m,v){const r=n("ExternalLinkIcon");return s(),i("div",null,[h,e("blockquote",null,[e("p",null,[e("a",d,[t("英語全文"),o(r)]),t("摘自ICOMOS。"),_,e("a",p,[t("日語全文"),o(r)]),t("摘自ICOMOS Japan。")])]),u])}const k=a(l,[["render",f],["__file","1904-the-preservation-and-restoration-of-architectural-mounments.html.vue"]]);export{k as default};
